package oopproj;

public class Item {
	
	private int id;
	private String name;
	private float price;
	
	public Item() {
		// TODO Auto-generated constructor stub
	}

	//getter method
	public int getId() {
		return id;
	}
	
	//setter method
	public void setId(int id) {
		this.id = id;
	}

	//getter method
	public String getName() {
		return name;
	}

	//setter method
	public void setName(String name) {
		this.name = name;
	}

	//getter method
	public float getPrice() {
		return price;
	}

	//setter method
	public void setPrice(float price) {
		this.price = price;
	}
	
	

}
